function loginx() {

  if (ajaxStart == false) {
    return false;
  }
  var site_url = "yj-u.com";
  var location = localStorage.getItem('location');
  var provider = localStorage.getItem('provider');
  //var sID = $("#sID2").val();
  //var sPASS = $("#sPASS2").val();
  var isRemembered = $(".auto_login").prop('checked');

  if (sID == '' || sPASS == '') {
    $("#sID2").addClass('error');
    $("#sPASS2").addClass('error');
    $(".sbmt-login").removeClass('is-loading disabled');
    ajaxStart = false;
  } else {
    var data = {
      'username': sID,
      'password': sPASS,
    };
    $.ajax({
      url: '/api/login',
      type: 'POST',
      data: data,
      dataType: "json",
      success: function(result) {
        $(".sbmt-login").removeClass('is-loading disabled');
        if (result.error==false) {
          if (isRemembered) {
            localStorage.setItem("sID", sID);
            localStorage.setItem("sPASS", sPASS);
          } else {
            localStorage.setItem("sID", "");
            localStorage.setItem("sPASS", "");
          }
          window.location.href = "/";
        } else {
          swal({
            title: "Oops!",
            text: result.msg,
            type: "error"
          }).then(function() {
            window.location.reload();
          });
        }
        ajaxStart = false;
      },
      error: function(xhr, status, error) {
        console.log("XHR:", xhr);
  console.log("Status:", status);
  console.log("Error:", error);
  alert("HTTP 상태: " + xhr.status + "\n응답 텍스트: " + xhr.responseText);
      }
    });
  }
}
