                alertify.alert('알림', '아이디와 비밀번호를 입력하세요.');
            }
        }
    );
    function loginSubmit() {
      
      if ($("input[name='mb_login_id']").val() == "아이디" || $("input[name='mb_login_id']").val() == "") {
          alert("로그인 아이디를 입력해 주세요");
          frm.userid.focus();
          return;
      }
      if ($("input[name='password']").val() == "******" || $("input[name='password']").val() == "") {
          alert("비밀번호를 입력해 주세요");
          frm.password.focus();
          return;
      }

      // frm.action = "/login/login.asp";
      // frm.submit();
      var username = $("input[name='mb_login_id']").val();
      var password = $("input[name='password']").val();

      var formData = new FormData();
      formData.append("_token", $("#_token").val());
      formData.append("username", username);
      formData.append("password", password);

      $.ajax({
          type: "POST",
          url: "/api/login",
          data: formData,
          processData: false,
          contentType: false,
          cache: false,
          async: false,
          success: function (data, status) {
              if (data.error) {
                  alert('아이디 또는 비밀번호가 잘못되었습니다.');
              }

              location.reload();
          },
          error: function (err, xhr) {
          }
      });
  }
</script>
